import HomeView from "./home.view";
import React, { useState, useEffect } from 'react';

const Home = () => {
  const [products, setProducts] = useState([]);
  const ApiCall = () => {
    fetch('products.json', {
      headers : { 
        'Content-Type': 'application/json',
        'Accept': 'application/json'
       }
    }
  )
    .then(response => {
        if (!response.ok) {
            throw new Error("HTTP error " + response.status);
        }
        return response.json();
    })
    .then(data => {
      setProducts(data);
    })
    .catch(function () {
        const dataError = true;
    })
  }

  useEffect(() => {
    ApiCall();
  }, [])

  if (!products.length) {
    ApiCall();
  }

  debugger;

  return products.length ? <HomeView products={products} /> : <div>loading...</div>;
}

export default Home;
