import HomeView from "./home.view";
import React, { useState, useEffect } from 'react';

const Home = () => {
  const [products, setProducts] = useState([]);
  const [responseError, setResponseError] = useState(false);

  const buildProducts = () => {
    fetch('products.json', {
      headers : { 
        'Content-Type': 'application/json',
        'Accept': 'application/json'
       }
    }
  )
    .then(response => {
        if (!response.ok) {
            throw new Error("HTTP error " + response.status);
        }
        return response.json();
    })
    .then(data => {
      setProducts(data);
    })
    .catch(function () {
      setResponseError(true);
    })
  }

  const handleSearch = (value) => {
    if (!value) {
      return buildProducts();
    }

    const newProducts = products.filter(a => a.name.includes(value));
    if (!newProducts.length) {
      newProducts-push("could not find any product");
    }
    setProducts(newProducts);
  }

  useEffect(() => {
    buildProducts();
  }, []);

  return products.length || responseError ? <HomeView products={products} handleSearch={handleSearch} /> : <div>loading...</div>;
}

export default Home;
